package day23_arrays_and_loops;

public class PrintLetters {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1; i<3; i++) {
			
			for (char letter='a'; letter<='z'; letter++) {
				System.out.print(letter+",");
			}
			System.out.println("\n************************** **************************");
		}

	}

}
